import React, { useState } from "react";
import {
  VStack,
  HStack,
  Button,
  IconButton,
  Icon,
  Text,
  NativeBaseProvider,
  Center,
  Box,
  StatusBar,
  View,
  Heading,
  FormControl,
  Input,
  Link,
  Pressable,
} from "native-base";
import {
  MaterialIcons,
  Feather,
  MaterialCommunityIcons,
} from "@expo/vector-icons";

function HeaderAppBar() {
  return (
    <>
      <StatusBar bg="#3700B3" barStyle="light-content" />
      <Box safeAreaTop bg="#6200ee" />
      <HStack
        bg="#6200ee"
        px="1"
        py="3"
        justifyContent="space-between"
        alignItems="center"
        w="100%"
        maxW="100%"
      >
        <HStack alignItems="center">
          <IconButton
            onPress={() => navigation.navigate("a1")}
            icon={
              <Icon size="sm" as={MaterialIcons} name="add" color="white" />
            }
          />
          <Text color="white" fontSize="20" fontWeight="bold">
            Аймаг
          </Text>
        </HStack>
        <HStack>
          {/* <IconButton icon={<Icon as={Feather} name="map" size="sm" color="white" />} />
          <IconButton icon={<Icon as={MaterialIcons} name="search" size="sm" color="white" />} /> */}
          <IconButton
            icon={
              <Icon
                as={MaterialIcons}
                name="ios-share"
                size="sm"
                color="white"
              />
            }
          />
        </HStack>
      </HStack>
    </>
  );
}

// http://oblador.github.io/react-native-vector-icons/   --Icons
// (hvz base, Native Base)

function Header() {
  return (
    <Center>
      <HeaderAppBar />
    </Center>
  );
}

function BottomTab() {
  const [selected, setSelected] = useState(1);
  return (
    <NativeBaseProvider>
      <Box
        flex={1}
        bg="white"
        safeAreaTop
        width="100%"
        maxW="100%"
        alignSelf="center"
      >
        <Center flex={1}></Center>
        <HStack bg="indigo.600" alignItems="center" safeAreaBottom shadow={6}>
          <Pressable
            style={{ cursor: "pointer" }}
            opacity={selected === 0 ? 1 : 0.5}
            py="3"
            flex={1}
            onPress={() => setSelected(0)}
          >
            <Center>
              <Icon
                mb="1"
                as={
                  <MaterialCommunityIcons
                    name={selected === 0 ? "home" : "home-outline"}
                  />
                }
                color="white"
                size="sm"
              />
              <Text color="white" fontSize="12">
                Aimag
              </Text>
            </Center>
          </Pressable>
          <Pressable
            style={{ cursor: "pointer" }}
            opacity={selected === 1 ? 1 : 0.5}
            py="2"
            flex={1}
            onPress={() => setSelected(1)}
          >
            <Center>
              <Icon
                mb="1"
                as={<MaterialIcons name="search" />}
                color="white"
                size="sm"
              />
              <Text color="white" fontSize="12">
                Sum
              </Text>
            </Center>
          </Pressable>
          <Pressable
            style={{ cursor: "pointer" }}
            opacity={selected === 2 ? 1 : 0.6}
            py="2"
            flex={1}
            onPress={() => setSelected(2)}
          >
            <Center>
              <Icon
                mb="1"
                as={
                  <MaterialCommunityIcons
                    name={selected === 2 ? "cart" : "cart-outline"}
                  />
                }
                color="white"
                size="sm"
              />
              <Text color="white" fontSize="12">
                About Us
              </Text>
            </Center>
          </Pressable>
        </HStack>
      </Box>
    </NativeBaseProvider>
  );
}
export default ({ navigation }) => {
  return (
    <NativeBaseProvider>
      <View flex={1} px="0">
        <Header />
        <BottomTab />
      </View>
    </NativeBaseProvider>
  );
};
