import React, { useState } from "react";
import { StatusBar } from "expo-status-bar";
import {
  StyleSheet,
  Text,
  View,
  Button,
  Image,
  Modal,
  TouchableOpacity,
} from "react-native";
import { WebView } from "react-native-webview";

export default function App({ navigation }) {
  // initial state
  const [isVisible, setIsVisible] = useState(false);
  // hide show modal
  let displayModal = (show) => {
    setIsVisible(show);
  };
  return (
    <View style={styles.container}>
      <Modal visible={isVisible}>
        <View style={{ minHeight: 300 }}>
          <WebView
            source={{ uri: "https://www.youtube.com/embed/MhkGQAoc7bc" }}
            style={styles.video}
          />
          <Button title="CLOSE" onPress={() => displayModal(false)} />
        </View>
      </Modal>

      <TouchableOpacity
        style={styles.button}
        onPress={() => {
          displayModal(true);
        }}
      >
        <Text style={styles.buttonText}>Show Modal</Text>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  containerW: {
    flex: 1,
  },
  container: {
    padding: 25,
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
  },
  video: {
    marginTop: 20,
    maxHeight: 200,
    width: 320,
    flex: 1,
  },
  button: {
    display: "flex",
    height: 60,
    borderRadius: 6,
    justifyContent: "center",
    alignItems: "center",
    width: "100%",
    backgroundColor: "#2AC062",
    shadowColor: "#2AC062",
    shadowOpacity: 0.5,
    shadowOffset: {
      height: 10,
      width: 0,
    },
    shadowRadius: 25,
  },
  closeButton: {
    display: "flex",
    height: 60,
    borderRadius: 6,
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "#FF3974",
    shadowColor: "#2AC062",
    shadowOpacity: 0.5,
    shadowOffset: {
      height: 10,
      width: 0,
    },
    shadowRadius: 25,
  },
  buttonText: {
    color: "#FFFFFF",
    fontSize: 22,
  },
  image: {
    marginTop: 150,
    marginBottom: 10,
    width: "100%",
    height: 350,
  },
  text: {
    fontSize: 24,
    marginBottom: 30,
    padding: 40,
  },
});
